import React from 'react';
import { StyleSheet, Button, View, Text, TouchableOpacity} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { Picker } from '@react-native-picker/picker'; 
import { Camera } from 'expo-camera';
var camera = Camera;

function HomeScreen({ navigation }) {
  const [startCamera, setStartCamera] = React.useState(false)
  const __startCamera = async () => {
    const {status} = await Camera.requestPermissionsAsync()
    console.log(status)
    if (status === 'granted') {
      setStartCamera(true)
    } else {
      Alert.alert('Access denied')
    }
  }
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Home Screen</Text>
      <Button
        title="Enter Data Manually"
        onPress={() => navigation.navigate('Manual')}
      />
      <Button
        title="Enter data from Camera"
        onPress={() => navigation.navigate('Camera'), __startCamera}
      />
    </View>
  );
}

function ManualScreen({ navigation }) {
  return (
    <View style={styles.container}>
    <View style={styles.section}>
      <Text size={30} color="#19e7f7">
        Manual Entry
      </Text>
      <Button title="Go to Home" onPress={() => navigation.navigate('Home')} />
    </View>
    <View style={styles.componentsSection}>
        <Text style={styles.componentSectionHeader}>Select food: </Text>
        <Picker style={{ width: '100%', alignSelf: 'center', color: '#66fcf1' }}>
          <Picker.Item label="Bread" value="Bread" />
          <Picker.Item label="Rice" value="Rice" />
        </Picker>
        <View style={styles.buttonStyle}>
            <Button title="Add item" />
        </View>
      </View>
      <View style={styles.section}>
        <View style={styles.buttonStyle}>
            <Button title="Done" />
        </View>
      </View>
  </View>
  );
}

function CameraScreen({ navigation }){
  const __takePicture = async () => {
    const photo = await camera.takePictureAsync()
    console.log(photo)
    setPreviewVisible(true)
    //setStartCamera(false)
    setCapturedImage(photo)
  }
  return(
  <Camera 
    style={{flex: 1,width:"100%"}}
    ref={(r) => {
    camera = r
    }}
  > 
   <View
        style={{
        position: 'absolute',
        bottom: 0,
        flexDirection: 'row',
        flex: 1,
        width: '100%',
        padding: 20,
        justifyContent: 'space-between'
        }}
      >
        <View
        style={{
        alignSelf: 'center',
        flex: 1,
        alignItems: 'center'
        }}
        >
            <TouchableOpacity
            onPress={__takePicture}
            style={{
            width: 70,
            height: 70,
            bottom: 0,
            borderRadius: 50,
            backgroundColor: '#fff'
            }}
            />
    </View>
    </View>
  </Camera>
  )
}

const Stack = createStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="Manual" component={ManualScreen} />
        <Stack.Screen name="Camera" component={CameraScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#C5C6C7',
    paddingHorizontal: 15,
    paddingTop: 20,
  },
  buttonStyle: {
    borderColor: '#66FDF1',
    borderWidth: 4,
    marginTop: 50,
    marginHorizontal: 100,
  },
  section: {
    marginBottom: 30,
    paddingHorizontal: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },
  sectionHeader: {
    marginBottom: 8,
  },
  componentsSection: {
    backgroundColor: '#1f2833',
    paddingHorizontal: 16,
    paddingVertical: 24,
    marginBottom: 20,
    borderRadius: 5,
    borderColor: '#c5c6c7',
    borderWidth: 4
  },
  componentSectionHeader: {
    // fontFamily: fonts.primaryRegular,
    color: '#ffffff',
    fontSize: 24,
    marginBottom: 20,
  },
  demoButtonsContainer: {
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  demoIconsContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    flex: 1,
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 3,
    marginBottom: 20,
  },
  demoButton: {
    marginTop: 8,
    marginBottom: 8,
  },
  demoItem: {
    marginVertical: 15,
  },
});



export default App;
